    public void llenarBoxNombrePlanEstudio(){
        boxNombrePlanEstudios.removeAllItems();
        try {
            ResultSet resultado= Splash.conexionBaseDatos.consulta("select Nombre from Plan_Estudios;");
            while(resultado.next()){
                boxNombrePlanEstudios.addItem(resultado.getString(1));
            }
        } catch (Exception e) {
            System.out.println("Error");
        }
    }

